mapper.update:
		update tb_order 
		set customerUuid=#{customerUuid},
			orderTime=#{orderTime},
   			totalMoney=#{totalMoney} 
   			saveMoney=#{saveMoney} 
   			state=#{state} 
   	  	where uuid=#{uuid}

mapper.select:
		select * from tb_order
		
			
        		and uuid=#{uuid}
        	  
        	
        		and customerUuid=#{customerUuid}
        	
        	
        		and orderTime=#{orderTime}
        	
        	
        		and totalMoney=#{totalMoney}
        	
        	
        		and saveMoney=#{saveMoney}
        	
        	
        		and state=#{state}
